:root {
	--background: oklch(0.9930 0.0017 145.5624);
	--foreground: oklch(0.2520 0.0502 154.7418);
	--card: oklch(1.0000 0 0);
	--card-foreground: oklch(0.2520 0.0502 154.7418);
	--popover: oklch(1.0000 0 0);
	--popover-foreground: oklch(0.2520 0.0502 154.7418);
	--primary: oklch(0.5406 0.0773 152.7118);
	--primary-foreground: oklch(1.0000 0 0);
	--secondary: oklch(0.9663 0.0058 153.7751);
	--secondary-foreground: oklch(0.3260 0.0603 151.0783);
	--muted: oklch(0.9663 0.0058 153.7751);
	--muted-foreground: oklch(0.5627 0.0263 153.0551);
	--accent: oklch(0.9482 0.0109 149.8568);
	--accent-foreground: oklch(0.3260 0.0603 151.0783);
	--destructive: oklch(0.6368 0.2078 25.3313);
	--destructive-foreground: oklch(1.0000 0 0);
	--border: oklch(0.9006 0.0145 148.7651);
	--input: oklch(0.9006 0.0145 148.7651);
	--ring: oklch(0.5406 0.0773 152.7118);
	--chart-1: oklch(0.5406 0.0773 152.7118);
	--chart-2: oklch(0.7086 0.0151 155.4568);
	--chart-3: oklch(0.6146 0.0239 153.8204);
	--chart-4: oklch(0.5180 0.0258 155.3741);
	--chart-5: oklch(0.4220 0.0261 153.3791);
	--sidebar: oklch(0.9663 0.0058 153.7751);
	--sidebar-foreground: oklch(0.3260 0.0603 151.0783);
	--sidebar-primary: oklch(0.5406 0.0773 152.7118);
	--sidebar-primary-foreground: oklch(1.0000 0 0);
	--sidebar-accent: oklch(0.9482 0.0109 149.8568);
	--sidebar-accent-foreground: oklch(0.3260 0.0603 151.0783);
	--sidebar-border: oklch(0.9006 0.0145 148.7651);
	--sidebar-ring: oklch(0.5406 0.0773 152.7118);
	--radius: 0.5rem;
	--shadow-2xs: 0px 4px 8px 0px hsl(0 0% 0% / 0.03);
	--shadow-xs: 0px 4px 8px 0px hsl(0 0% 0% / 0.03);
	--shadow-sm: 0px 4px 8px 0px hsl(0 0% 0% / 0.05), 0px 1px 2px -1px hsl(0 0% 0% / 0.05);
	--shadow: 0px 4px 8px 0px hsl(0 0% 0% / 0.05), 0px 1px 2px -1px hsl(0 0% 0% / 0.05);
	--shadow-md: 0px 4px 8px 0px hsl(0 0% 0% / 0.05), 0px 2px 4px -1px hsl(0 0% 0% / 0.05);
	--shadow-lg: 0px 4px 8px 0px hsl(0 0% 0% / 0.05), 0px 4px 6px -1px hsl(0 0% 0% / 0.05);
	--shadow-xl: 0px 4px 8px 0px hsl(0 0% 0% / 0.05), 0px 8px 10px -1px hsl(0 0% 0% / 0.05);
	--shadow-2xl: 0px 4px 8px 0px hsl(0 0% 0% / 0.13);
	--tracking-normal: 0rem;
	--spacing: 0.25rem;
  }

  .dark {
	--background: oklch(0.2100 0.0119 156.0516);
	--foreground: oklch(0.9663 0.0058 153.7751);
	--card: oklch(0.2100 0.0119 156.0516);
	--card-foreground: oklch(0.9663 0.0058 153.7751);
	--popover: oklch(0.2100 0.0119 156.0516);
	--popover-foreground: oklch(0.9663 0.0058 153.7751);
	--primary: oklch(0.5406 0.0773 152.7118);
	--primary-foreground: oklch(1.0000 0 0);
	--secondary: oklch(0.3306 0.0285 154.5649);
	--secondary-foreground: oklch(0.9034 0.0132 130.7888);
	--muted: oklch(0.3306 0.0285 154.5649);
	--muted-foreground: oklch(0.7086 0.0151 155.4568);
	--accent: oklch(0.3306 0.0285 154.5649);
	--accent-foreground: oklch(0.9034 0.0132 130.7888);
	--destructive: oklch(0.6786 0.2095 24.6583);
	--destructive-foreground: oklch(0.2100 0.0119 156.0516);
	--border: oklch(0.3306 0.0285 154.5649);
	--input: oklch(0.3306 0.0285 154.5649);
	--ring: oklch(0.5406 0.0773 152.7118);
	--chart-1: oklch(0.5406 0.0773 152.7118);
	--chart-2: oklch(0.7086 0.0151 155.4568);
	--chart-3: oklch(0.6146 0.0239 153.8204);
	--chart-4: oklch(0.5180 0.0258 155.3741);
	--chart-5: oklch(0.4220 0.0261 153.3791);
	--sidebar: oklch(0.2100 0.0119 156.0516);
	--sidebar-foreground: oklch(0.9034 0.0132 130.7888);
	--sidebar-primary: oklch(0.5406 0.0773 152.7118);
	--sidebar-primary-foreground: oklch(1.0000 0 0);
	--sidebar-accent: oklch(0.3306 0.0285 154.5649);
	--sidebar-accent-foreground: oklch(0.9034 0.0132 130.7888);
	--sidebar-border: oklch(0.3306 0.0285 154.5649);
	--sidebar-ring: oklch(0.5406 0.0773 152.7118);
	--radius: 0.5rem;
	--shadow-2xs: 0px 2px 4px 0px hsl(0 0% 0% / 0.05);
	--shadow-xs: 0px 2px 4px 0px hsl(0 0% 0% / 0.05);
	--shadow-sm: 0px 2px 4px 0px hsl(0 0% 0% / 0.10), 0px 1px 2px -1px hsl(0 0% 0% / 0.10);
	--shadow: 0px 2px 4px 0px hsl(0 0% 0% / 0.10), 0px 1px 2px -1px hsl(0 0% 0% / 0.10);
	--shadow-md: 0px 2px 4px 0px hsl(0 0% 0% / 0.10), 0px 2px 4px -1px hsl(0 0% 0% / 0.10);
	--shadow-lg: 0px 2px 4px 0px hsl(0 0% 0% / 0.10), 0px 4px 6px -1px hsl(0 0% 0% / 0.10);
	--shadow-xl: 0px 2px 4px 0px hsl(0 0% 0% / 0.10), 0px 8px 10px -1px hsl(0 0% 0% / 0.10);
	--shadow-2xl: 0px 2px 4px 0px hsl(0 0% 0% / 0.25);
  }

  @theme inline {
	--color-background: var(--background);
	--color-foreground: var(--foreground);
	--color-card: var(--card);
	--color-card-foreground: var(--card-foreground);
	--color-popover: var(--popover);
	--color-popover-foreground: var(--popover-foreground);
	--color-primary: var(--primary);
	--color-primary-foreground: var(--primary-foreground);
	--color-secondary: var(--secondary);
	--color-secondary-foreground: var(--secondary-foreground);
	--color-muted: var(--muted);
	--color-muted-foreground: var(--muted-foreground);
	--color-accent: var(--accent);
	--color-accent-foreground: var(--accent-foreground);
	--color-destructive: var(--destructive);
	--color-destructive-foreground: var(--destructive-foreground);
	--color-border: var(--border);
	--color-input: var(--input);
	--color-ring: var(--ring);
	--color-chart-1: var(--chart-1);
	--color-chart-2: var(--chart-2);
	--color-chart-3: var(--chart-3);
	--color-chart-4: var(--chart-4);
	--color-chart-5: var(--chart-5);
	--color-sidebar: var(--sidebar);
	--color-sidebar-foreground: var(--sidebar-foreground);
	--color-sidebar-primary: var(--sidebar-primary);
	--color-sidebar-primary-foreground: var(--sidebar-primary-foreground);
	--color-sidebar-accent: var(--sidebar-accent);
	--color-sidebar-accent-foreground: var(--sidebar-accent-foreground);
	--color-sidebar-border: var(--sidebar-border);
	--color-sidebar-ring: var(--sidebar-ring);

	--font-sans: var(--font-sans);
	--font-mono: var(--font-mono);
	--font-serif: var(--font-serif);

	--radius-sm: calc(var(--radius) - 4px);
	--radius-md: calc(var(--radius) - 2px);
	--radius-lg: var(--radius);
	--radius-xl: calc(var(--radius) + 4px);

	--shadow-2xs: var(--shadow-2xs);
	--shadow-xs: var(--shadow-xs);
	--shadow-sm: var(--shadow-sm);
	--shadow: var(--shadow);
	--shadow-md: var(--shadow-md);
	--shadow-lg: var(--shadow-lg);
	--shadow-xl: var(--shadow-xl);
	--shadow-2xl: var(--shadow-2xl);

	--tracking-tighter: calc(var(--tracking-normal) - 0.05em);
	--tracking-tight: calc(var(--tracking-normal) - 0.025em);
	--tracking-normal: var(--tracking-normal);
	--tracking-wide: calc(var(--tracking-normal) + 0.025em);
	--tracking-wider: calc(var(--tracking-normal) + 0.05em);
	--tracking-widest: calc(var(--tracking-normal) + 0.1em);
  }

  body {
	letter-spacing: var(--tracking-normal);
  }

@tailwind base;
@tailwind components;
@tailwind utilities;

@layer utilities {
  .text-balance {
    text-wrap: balance;
  }
}

@layer base {
  * {
    @apply border-border;
  }
}

import type { Config } from "tailwindcss";

const config: Config = {
  content: [
    "./pages/**/*.{ts,tsx,js,jsx,mdx}",
    "./components/**/*.{ts,tsx,js,jsx,mdx}",
    "./app/**/*.{ts,tsx,js,jsx,mdx}",
    "*.{ts,tsx,js,jsx,mdx}",
  ],
  theme: {
    extend: {
      colors: {
        background: "var(--background)",
        foreground: "var(--foreground)",

        card: "var(--card)",
        "card-foreground": "var(--card-foreground)",

        popover: "var(--popover)",
        "popover-foreground": "var(--popover-foreground)",

        primary: "var(--primary)",
        "primary-foreground": "var(--primary-foreground)",

        secondary: "var(--secondary)",
        "secondary-foreground": "var(--secondary-foreground)",

        muted: "var(--muted)",
        "muted-foreground": "var(--muted-foreground)",

        accent: "var(--accent)",
        "accent-foreground": "var(--accent-foreground)",

        destructive: "var(--destructive)",
        "destructive-foreground": "var(--destructive-foreground)",

        border: "var(--border)",
        input: "var(--input)",
        ring: "var(--ring)",

        chart: {
          1: "var(--chart-1)",
          2: "var(--chart-2)",
          3: "var(--chart-3)",
          4: "var(--chart-4)",
          5: "var(--chart-5)",
        },

        sidebar: {
          DEFAULT: "var(--sidebar)",
          foreground: "var(--sidebar-foreground)",
          primary: "var(--sidebar-primary)",
          "primary-foreground": "var(--sidebar-primary-foreground)",
          accent: "var(--sidebar-accent)",
          "accent-foreground": "var(--sidebar-accent-foreground)",
          border: "var(--sidebar-border)",
          ring: "var(--sidebar-ring)",
        },
      },
      borderRadius: {
        lg: "var(--radius)",
        md: "calc(var(--radius) - 2px)",
        sm: "calc(var(--radius) - 4px)",
      },
      boxShadow: {
        "2xs": "var(--shadow-2xs)",
        xs: "var(--shadow-xs)",
        sm: "var(--shadow-sm)",
        DEFAULT: "var(--shadow)",
        md: "var(--shadow-md)",
        lg: "var(--shadow-lg)",
        xl: "var(--shadow-xl)",
        "2xl": "var(--shadow-2xl)",
      },
      fontFamily: {
        serif: ["var(--font-serif)", "sans-serif"],
        sans: ["var(--font-sans)", "sans-serif"],
        mono: ["var(--font-mono)", "monospace"],
      },
      keyframes: {
        "fade-in-scale": {
          "0%": { opacity: "0", transform: "scale(0.95)" },
          "100%": { opacity: "1", transform: "scale(1)" },
        },
      },
      animation: {
        "fade-in-scale": "fade-in-scale 0.3s ease-out",
      },
    },
  },
  plugins: [require("tailwindcss-animate")],
};
export default config;
